const body = $input.first().json.body || $input.first().json;
const target_language = body.target_language || 'pl';
const validLangs = ['pl', 'ro', 'ru'];
if (!validLangs.includes(target_language)) {
  throw new Error('Invalid language: ' + target_language + '. Must be one of: ' + validLangs.join(', '));
}
const langNames = { pl: 'Polnisch', ro: 'Rum\u00e4nisch', ru: 'Russisch' };
return [{ json: { target_language, lang_name: langNames[target_language] } }];

const lang = $('Parse Request').first().json.target_language;
const field = 'title_' + lang;
const rows = $input.all().map(r => r.json);
const untranslated = rows.filter(r => !r[field]);

if (untranslated.length === 0) {
  return [{ json: { positions: [], count: 0, skip: true, batches: [], batchCount: 0 } }];
}

// Batch into groups of 30 for API calls
const batches = [];
for (let i = 0; i < untranslated.length; i += 30) {
  batches.push(untranslated.slice(i, i + 30));
}

return [{ json: {
  batches,
  count: untranslated.length,
  batchCount: batches.length,
  skip: false
} }];

const lang = $('Parse Request').first().json.target_language;
const langName = $('Parse Request').first().json.lang_name;
const positions = $input.first().json.positions;

// Build the prompt
const positionList = positions.map(p =>
  `${p.position_number}: ${p.title}`
).join('\n');

const countryMap = { Polnisch: 'Polen', 'Rum\u00e4nisch': 'Rum\u00e4nien', Russisch: 'Russland' };
const country = countryMap[langName] || langName;

const prompt = `\u00dcbersetze folgende Bauleistungstexte von Deutsch nach ${langName}.\nKontext: Das sind Arbeitsanweisungen f\u00fcr Handwerker auf Baustellen (Maler, Fliesenleger, Elektriker, Sanit\u00e4r).\nVerwende Fachbegriffe die Handwerker in ${country} verstehen.\n\nAntworte NUR mit einem JSON-Array im Format: [{"nr": "1.1", "translation": "..."}]\n\nTexte:\n${positionList}`;

return [{ json: {
  prompt,
  positions: positions.map(p => ({ id: p.id, nr: p.position_number })),
  lang
} }];

const lang = $input.first().json.lang;
const positions = $input.first().json.positions;

// In production: parse AI response and build SQL updates
// The actual updates would look like:
// UPDATE catalog_positions_v2 SET title_pl = 'translated text' WHERE id = 'uuid';

return [{ json: { saved: positions.length, lang } }];

