parameters.jsCode:
// Extract event data from router payload (webhook path)
const input = $input.first().json;

const eventId = input.body?.event_id || input.event_id;
const projectId = input.body?.project_id || input.project_id;
const payload = input.body?.payload || input.payload || {};

const purchaseInvoiceId = payload.entity_id || input.body?.entity_id || input.entity_id;
const stepKey = `lexware_push:${purchaseInvoiceId}`;

return {
  json: {
    event_id: eventId,
    project_id: projectId || null,
    purchase_invoice_id: purchaseInvoiceId,
    step_key: stepKey,
    source: 'webhook'
  }
};

// Parse schedule-fetched events into individual items
const items = $input.all();

return items.map(item => ({
  json: {
    event_id: item.json.event_id,
    project_id: item.json.project_id || null,
    purchase_invoice_id: item.json.entity_id,
    step_key: `lexware_push:${item.json.entity_id}`,
    source: 'schedule'
  }
}));

// Resolve event data from whichever trigger path ran
// Both Parse nodes output identical fields: event_id, project_id, purchase_invoice_id, step_key, source
let eventData;
try {
  eventData = $('Parse Event (Webhook)').first().json;
} catch(e) {
  eventData = $('Parse Events (Schedule)').first().json;
}

return { json: eventData };

