// Build Lexware contact payload for supplier
const inv = $('Load Invoice + Supplier').first().json;

const contact = {
  version: 0,
  roles: {
    vendor: {}
  },
  company: {
    name: inv.supplier_name
  }
};

// Add address if available
if (inv.supplier_street && inv.supplier_zip && inv.supplier_city) {
  contact.addresses = {
    billing: [{
      street: inv.supplier_street,
      zip: inv.supplier_zip,
      city: inv.supplier_city,
      countryCode: 'DE'
    }]
  };
}

return {
  json: {
    contact_payload: contact,
    supplier_id: inv.supplier_id,
    supplier_name: inv.supplier_name
  }
};

// Build Lexware voucher payload for purchase invoice
const inv = $('Load Invoice + Supplier').first().json;

// Get lexware_contact_id from either existing or freshly created
let contactId = inv.lexware_contact_id || null;
try {
  const saved = $('Save Contact ID').first().json;
  if (saved?.lexware_contact_id) contactId = saved.lexware_contact_id;
} catch(e) {
  // Save Contact ID didn't run (had contact already, or contact error path)
}

// Map BG expense_category ‚Üí Lexware categoryId
const categoryMap = {
  'SUBCONTRACTOR': '7a1efa0e-6283-4571-add5-b49ab2bcbc4d',  // Fremdleistungen
  'MATERIAL': '63c88f81-e02b-11e2-a28f-0800200c9a66',        // Wareneinkauf
  'VEHICLE_FUEL': '8f8664a8-fd86-11e1-a21f-0800200c9a66',    // Sonstige Ausgaben
  'VEHICLE_RENTAL': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'VEHICLE_REPAIR': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'ENTERTAINMENT': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'SOFTWARE': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'INSURANCE': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'OFFICE': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'DISPOSAL': '8f8664a8-fd86-11e1-a21f-0800200c9a66',
  'OTHER': '8f8664a8-fd86-11e1-a21f-0800200c9a66'
};

const categoryId = categoryMap[inv.expense_cat] || '8f8664a8-fd86-11e1-a21f-0800200c9a66';

// Format dates to Lexware RFC 3339 format
const formatDate = (d) => {
  if (!d) return null;
  // Handle ISO string or date-only string
  const dateStr = typeof d === 'string' ? d.substring(0, 10) : d;
  return `${dateStr}T00:00:00.000+01:00`;
};

const voucher = {
  type: 'purchaseinvoice',
  voucherNumber: inv.invoice_number,
  voucherDate: formatDate(inv.invoice_date),
  totalGrossAmount: parseFloat(inv.total_gross),
  totalTaxAmount: parseFloat(inv.vat_amount || 0),
  taxType: 'gross',
  remark: 'Automatisch importiert aus BauGenius',
  voucherItems: [{
    amount: parseFloat(inv.total_gross),
    taxAmount: parseFloat(inv.vat_amount || 0),
    taxRatePercentage: parseFloat(inv.vat_percent || 19),
    categoryId: categoryId
  }]
};

// Optional fields
if (inv.due_date) voucher.dueDate = formatDate(inv.due_date);
if (contactId) voucher.contactId = contactId;

return {
  json: {
    voucher_payload: voucher,
    invoice_id: inv.id,
    invoice_number: inv.invoice_number,
    project_id: inv.project_id || null,
    pdf_storage_path: inv.pdf_storage_path,
    contact_id: contactId,
    category_id: categoryId
  }
};

