// Handle API error from Create Lexware Voucher
const input = $input.first().json;
const invoiceData = $('Build Voucher Payload').first().json;
const eventData = $('Event Data').first().json;

const statusCode = input.statusCode || input.status || 'unknown';
const errorBody = input.body || input.message || JSON.stringify(input);
const errorMsg = typeof errorBody === 'string' ? errorBody : JSON.stringify(errorBody);

return {
  json: {
    error_message: `HTTP ${statusCode}: ${errorMsg.substring(0, 500)}`,
    status_code: statusCode,
    invoice_id: invoiceData.invoice_id,
    invoice_number: invoiceData.invoice_number,
    project_id: invoiceData.project_id || null,
    event_id: eventData.event_id,
    step_key: eventData.step_key
  }
};

// Handle contact creation error ‚Äî non-blocking, continue without contactId
const input = $input.first().json;
const inv = $('Load Invoice + Supplier').first().json;

const errorMsg = input.body?.message || input.message || JSON.stringify(input).substring(0, 500);

// Log but don't fail ‚Äî voucher can be created without contactId
return {
  json: {
    ...inv,
    contact_error: `Contact creation failed: ${errorMsg}`,
    lexware_contact_id: null
  }
};

// Step already done or in progress by another run
const claimResult = $input.first().json;
const eventData = $('Event Data').first().json;

return {
  json: {
    status: 'skipped',
    reason: claimResult.current_status === 'DONE'
      ? 'Already processed'
      : 'Step in progress by another run',
    current_status: claimResult.current_status,
    purchase_invoice_id: eventData.purchase_invoice_id
  }
};

// Resolve event data for not-claimed path
let eventData;
try {
  eventData = $('Parse Event (Webhook)').first().json;
} catch(e) {
  eventData = $('Parse Events (Schedule)').first().json;
}

return { json: eventData };
