parameters.jsCode:
const input = $input.first().json;
const eventId = input.body?.event_id || input.event_id;
const projectId = input.body?.project_id || input.project_id;
const payload = input.body?.payload || input.payload || {};

return {
  json: {
    event_id: eventId,
    project_id: projectId || null,
    purchase_invoice_id: payload.purchase_invoice_id,
    supplier_id: payload.supplier_id,
    supplier_name: payload.supplier_name || '',
    supplier_street: payload.supplier_street || '',
    supplier_zip: payload.supplier_zip || '',
    supplier_city: payload.supplier_city || '',
    step_key: `lexware_contact:${payload.supplier_id}`
  }
};

// Build Lexware contact payload
const e = $('Parse Event Data').first().json;
const contact = {
  version: 0,
  roles: { vendor: {} },
  company: { name: e.supplier_name }
};
if (e.supplier_street && e.supplier_zip && e.supplier_city) {
  contact.addresses = { billing: [{
    street: e.supplier_street, zip: e.supplier_zip,
    city: e.supplier_city, countryCode: 'DE'
  }]};
}
return { json: { contact_payload: contact } };

parameters.query:
UPDATE suppliers SET lexware_contact_id = '{{ $json.body.id }}', lexware_synced_at = now()
WHERE id = '{{ $('Parse Event Data').item.json.supplier_id }}'::uuid
RETURNING lexware_contact_id;

SELECT complete_workflow_step('{{ $('Parse Event Data').item.json.step_key }}', jsonb_build_object('lexware_contact_id', '{{ $json.lexware_contact_id }}'));

DO $$ BEGIN
  UPDATE events SET processed_at = now()
  WHERE id = '{{ $('Parse Event Data').item.json.event_id }}'::uuid;
EXCEPTION WHEN invalid_text_representation THEN NULL;
END $$;

INSERT INTO events (event_type, project_id, payload, source_system, source_flow, reference_table, reference_id)
VALUES (
  'LEXWARE_CONTACT_READY',
  {{ $('Parse Event Data').item.json.project_id ? "'" + $('Parse Event Data').item.json.project_id + "'::uuid" : "NULL::uuid" }},
  jsonb_build_object(
    'purchase_invoice_id', '{{ $('Parse Event Data').item.json.purchase_invoice_id }}',
    'lexware_contact_id', '{{ $json.lexware_contact_id }}'
  ),
  'n8n', 'M6_02b_Lexware_Contact_Sync',
  'suppliers', '{{ $('Parse Event Data').item.json.supplier_id }}'::uuid
)
RETURNING id, event_type::text;

SELECT complete_workflow_step('{{ $('Parse Event Data').item.json.step_key }}', '{"contact_error": true}');

DO $$ BEGIN
  UPDATE events SET processed_at = now()
  WHERE id = '{{ $('Parse Event Data').item.json.event_id }}'::uuid;
EXCEPTION WHEN invalid_text_representation THEN NULL;
END $$;

-- Emit CONTACT_READY anyway — voucher can be created without contact
INSERT INTO events (event_type, project_id, payload, source_system, source_flow, reference_table, reference_id)
VALUES (
  'LEXWARE_CONTACT_READY',
  {{ $('Parse Event Data').item.json.project_id ? "'" + $('Parse Event Data').item.json.project_id + "'::uuid" : "NULL::uuid" }},
  jsonb_build_object(
    'purchase_invoice_id', '{{ $('Parse Event Data').item.json.purchase_invoice_id }}',
    'lexware_contact_id', NULL
  ),
  'n8n', 'M6_02b_Lexware_Contact_Sync',
  'suppliers', '{{ $('Parse Event Data').item.json.supplier_id }}'::uuid
)
RETURNING id, event_type::text;
