parameters.jsCode:
const input = $input.first().json;
const eventId = input.body?.event_id || input.event_id;
const projectId = input.body?.project_id || input.project_id;
const payload = input.body?.payload || input.payload || {};

return {
  json: {
    event_id: eventId,
    project_id: projectId || null,
    purchase_invoice_id: payload.purchase_invoice_id,
    lexware_contact_id: payload.lexware_contact_id || null,
    step_key: `lexware_push:${payload.purchase_invoice_id}`
  }
};

const inv = $json;
const eventData = $('Parse Event Data').first().json;

// Use contact from event payload (freshly created) or from supplier record
const contactId = eventData.lexware_contact_id || inv.lexware_contact_id || null;

// Category mapping
const catMap = {
  'SUBCONTRACTOR': '7a1efa0e-6283-4571-add5-b49ab2bcbc4d',
  'MATERIAL': '63c88f81-e02b-11e2-a28f-0800200c9a66'
};
const categoryId = catMap[inv.expense_cat] || '8f8664a8-fd86-11e1-a21f-0800200c9a66';

const formatDate = (d) => d ? d.substring(0, 10) + 'T00:00:00.000+01:00' : null;

const voucher = {
  type: 'purchaseinvoice',
  voucherNumber: inv.invoice_number,
  voucherDate: formatDate(inv.invoice_date),
  totalGrossAmount: parseFloat(inv.total_gross),
  totalTaxAmount: parseFloat(inv.vat_amount || 0),
  taxType: 'gross',
  remark: 'Importiert aus BauGenius',
  voucherItems: [{
    amount: parseFloat(inv.total_gross),
    taxAmount: parseFloat(inv.vat_amount || 0),
    taxRatePercentage: parseFloat(inv.vat_percent || 19),
    categoryId: categoryId
  }]
};
if (inv.due_date) voucher.dueDate = formatDate(inv.due_date);
if (contactId) voucher.contactId = contactId;

return { json: { voucher_payload: voucher, invoice_id: inv.id, invoice_number: inv.invoice_number } };

parameters.query:
SELECT complete_workflow_step('{{ $('Parse Event Data').item.json.step_key }}',
  jsonb_build_object('lexware_voucher_id', '{{ $('Create Lexware Voucher').item.json.body.id }}'));

INSERT INTO lexware_sync_log (sync_direction, entity_type, entity_id, lexware_id, action, sync_status)
VALUES ('push', 'purchase_invoice', '{{ $('Build Voucher Payload').item.json.invoice_id }}'::uuid,
  '{{ $('Create Lexware Voucher').item.json.body.id }}', 'create', 'success');

DO $$ BEGIN
  UPDATE events SET processed_at = now()
  WHERE id = '{{ $('Parse Event Data').item.json.event_id }}'::uuid;
EXCEPTION WHEN invalid_text_representation THEN NULL;
END $$;

INSERT INTO events (event_type, project_id, payload, source_system, source_flow, reference_table, reference_id)
VALUES (
  'LEXWARE_PUSH_COMPLETED',
  {{ $('Parse Event Data').item.json.project_id ? "'" + $('Parse Event Data').item.json.project_id + "'::uuid" : "NULL::uuid" }},
  jsonb_build_object(
    'purchase_invoice_id', '{{ $('Build Voucher Payload').item.json.invoice_id }}',
    'lexware_voucher_id', '{{ $('Create Lexware Voucher').item.json.body.id }}',
    'invoice_number', '{{ $('Build Voucher Payload').item.json.invoice_number }}'
  ),
  'n8n', 'M6_02c_Lexware_Push_Voucher',
  'purchase_invoices', '{{ $('Build Voucher Payload').item.json.invoice_id }}'::uuid
)
RETURNING id, event_type::text;

