parameters.jsCode:
const input = $input.first().json;
const eventId = input.body?.event_id || input.event_id;
const projectId = input.body?.project_id || input.project_id;
const payload = input.body?.payload || input.payload || {};

// From M6_04a: payload contains the raw Lexware callback
const resourceId = payload.resourceId || '';

return {
  json: {
    event_id: eventId,
    project_id: projectId || null,
    lexware_resource_id: resourceId,
    step_key: `lexware_payment:${resourceId}`
  }
};

const resp = $input.first().json;
const body = resp.body || resp;

// Lexware payment has: id, voucherType, voucherStatus, openAmount, paidAmount, etc.
const voucherType = (body.voucherType || '').toLowerCase();
const voucherStatus = (body.voucherStatus || '').toLowerCase();
const relatedVoucherId = body.relatedVoucherId || body.voucherId || null;
const paidAmount = parseFloat(body.paidAmount || body.amount || 0);
const isPurchase = voucherType.includes('purchase') || voucherType.includes('voucher');
const isSales = voucherType.includes('invoice') || voucherType.includes('sales');

return {
  json: {
    lexware_payment_id: body.id || $('Parse Event Data').first().json.lexware_resource_id,
    related_voucher_id: relatedVoucherId,
    voucher_type: voucherType,
    voucher_status: voucherStatus,
    paid_amount: paidAmount,
    is_purchase: isPurchase,
    is_sales: isSales,
    payment_date: body.paymentDate || body.date || null
  }
};

