SELECT complete_workflow_step('{{ $('Parse Event Data').item.json.step_key }}',
  jsonb_build_object('paid_amount', {{ $('Parse Payment').item.json.paid_amount }}));

INSERT INTO lexware_sync_log (sync_direction, entity_type, lexware_id, action, sync_status)
VALUES ('pull', 'payment', '{{ $('Parse Event Data').item.json.lexware_resource_id }}', 'payment_update', 'success');

DO $$ BEGIN
  UPDATE events SET processed_at = now()
  WHERE id = '{{ $('Parse Event Data').item.json.event_id }}'::uuid;
EXCEPTION WHEN invalid_text_representation THEN NULL;
END $$;

INSERT INTO events (event_type, project_id, payload, source_system, source_flow)
VALUES (
  'LEXWARE_PAYMENT_RECEIVED',
  {{ $('Parse Event Data').item.json.project_id ? "'" + $('Parse Event Data').item.json.project_id + "'::uuid" : "NULL::uuid" }},
  jsonb_build_object(
    'lexware_payment_id', '{{ $('Parse Payment').item.json.lexware_payment_id }}',
    'paid_amount', {{ $('Parse Payment').item.json.paid_amount }},
    'voucher_type', '{{ $('Parse Payment').item.json.voucher_type }}'
  ),
  'n8n', 'M6_04b_Lexware_Payment_Handler'
)
RETURNING id, event_type::text;
