parameters.jsCode:
const input = $input.first().json;
const eventId = input.body?.event_id || input.event_id;
const projectId = input.body?.project_id || input.project_id;
const payload = input.body?.payload || input.payload || {};

const resourceId = payload.resourceId || '';

return {
  json: {
    event_id: eventId,
    project_id: projectId || null,
    lexware_invoice_id: resourceId,
    step_key: `lexware_invoice_sync:${resourceId}`
  }
};

const resp = $input.first().json;
const inv = resp.body || resp;
const eventData = $('Parse Event Data').first().json;

// Extract Lexware invoice fields
const invNumber = inv.voucherNumber || inv.invoiceNumber || '';
const customerName = inv.address?.name || '';
const customerAddr = [inv.address?.street, inv.address?.zip, inv.address?.city].filter(Boolean).join(', ');
const totalNet = parseFloat(inv.totalNetAmount || inv.lineItems?.reduce((s,i) => s + (parseFloat(i.netAmount || 0)), 0) || 0);
const totalGross = parseFloat(inv.totalGrossAmount || 0);
const totalVat = parseFloat(inv.totalTaxAmount || 0);
const invoiceDate = inv.voucherDate ? inv.voucherDate.substring(0, 10) : null;
const dueDate = inv.dueDate ? inv.dueDate.substring(0, 10) : null;
const contactId = inv.contactId || null;
const currency = inv.currency || 'EUR';
const paidAmount = parseFloat(inv.paidAmount || 0);

// Derive status
let status = 'OPEN';
if (inv.voucherStatus === 'paid' || inv.voucherStatus === 'paidoff') status = 'PAID';
else if (inv.voucherStatus === 'overdue') status = 'OVERDUE';
else if (inv.voucherStatus === 'voided') status = 'VOIDED';
else if (inv.voucherStatus === 'draft') status = 'DRAFT';

return {
  json: {
    lexware_invoice_id: eventData.lexware_invoice_id,
    invoice_number: invNumber,
    customer_name: customerName,
    customer_address: customerAddr,
    total_net: totalNet,
    total_gross: totalGross,
    total_vat: totalVat,
    invoice_date: invoiceDate,
    due_date: dueDate,
    lexware_contact_id: contactId,
    currency: currency,
    paid_amount: paidAmount,
    status: status,
    raw_data: inv
  }
};

parameters.query:
SELECT complete_workflow_step('{{ $('Parse Event Data').item.json.step_key }}',
  jsonb_build_object('invoice_number', '{{ $json.invoice_number }}', 'status', '{{ $json.status }}'));

INSERT INTO lexware_sync_log (sync_direction, entity_type, entity_id, lexware_id, action, sync_status)
VALUES ('pull', 'sales_invoice', '{{ $json.id }}'::uuid,
  '{{ $('Parse Event Data').item.json.lexware_invoice_id }}', 'upsert', 'success');

DO $$ BEGIN
  UPDATE events SET processed_at = now()
  WHERE id = '{{ $('Parse Event Data').item.json.event_id }}'::uuid;
EXCEPTION WHEN invalid_text_representation THEN NULL;
END $$;

INSERT INTO events (event_type, project_id, payload, source_system, source_flow, reference_table, reference_id)
VALUES (
  'LEXWARE_INVOICE_SYNCED',
  {{ $('Parse Event Data').item.json.project_id ? "'" + $('Parse Event Data').item.json.project_id + "'::uuid" : "NULL::uuid" }},
  jsonb_build_object(
    'lexware_invoice_id', '{{ $('Parse Event Data').item.json.lexware_invoice_id }}',
    'invoice_number', '{{ $json.invoice_number }}',
    'status', '{{ $json.status }}'
  ),
  'n8n', 'M6_04c_Lexware_Invoice_Sync',
  'sales_invoices', '{{ $json.id }}'::uuid
)
RETURNING id, event_type::text;
